import { useQuery } from "@tanstack/react-query";
import { Logo } from "@/components/ui/logo";
import { Clock } from "lucide-react";

interface OfferItem {
  id: number;
  name: string;
  price: number;
  isActive: boolean;
}

export default function Offers() {
  const { data: offers, isLoading } = useQuery<OfferItem[]>({
