@import url('https://fonts.googleapis.com/css2?family=Public+Sans:wght@100;200;300;400;500;600;700;800;900&display=swap');

@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    --color-yellow: 255, 213, 0;         /* #FFD500 - Pantone Yellow */
    --color-burgundy: 135, 37, 25;       /* #872519 */
    --color-orange: 232, 83, 3;          /* #E85303 */
    --color-turquoise: 54, 202, 176;     /* #36CAB0 */
  }

  @layer utilities {
    .hover\:text-shadow:hover {
      text-shadow: 0 0 8px currentColor, 0 0 12px rgba(currentColor, 0.3);
    }
  }

  * {
    @apply border-border;
  }

  @keyframes gradientAnimation {
    0% {
      background-position: 0% 50%;
    }
    50% {
      background-position: 100% 50%;
    }
    100% {
      background-position: 0% 50%;
    }
  }

  body {
    @apply font-['Public+Sans'] antialiased text-foreground;
    background: white;
    min-height: 100vh;
  }

  /* Gradient text effect for headings */
  .gradient-text {
    background: linear-gradient(90deg, 
      rgb(var(--color-burgundy)), 
      rgb(var(--color-orange)),
      rgb(var(--color-turquoise))
    );
    -webkit-background-clip: text;
    background-clip: text;
    color: transparent;
    background-size: 200% auto;
    animation: textGradient 8s linear infinite;
  }

  @keyframes textGradient {
    0% {
      background-position: 0% 50%;
    }
    50% {
      background-position: 100% 50%;
    }
    100% {
      background-position: 0% 50%;
    }
  }

  /* Gradient borders for elements */
  .gradient-border {
    position: relative;
    border: none;
  }

  .gradient-border::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    border-radius: inherit;
    padding: 2px;
    background: linear-gradient(45deg, 
      rgb(var(--color-burgundy)), 
      rgb(var(--color-orange)),
      rgb(var(--color-turquoise)),
      rgb(var(--color-burgundy))
    );
    background-size: 400% 400%;
    animation: gradientAnimation 8s ease infinite;
    -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    -webkit-mask-composite: xor;
    mask-composite: exclude;
    pointer-events: none;
  }

  /* Ensure good contrast for content areas */
  .card, 
  .navigation-menu,
  .dropdown-menu,
  .dialog {
    @apply bg-white/85 backdrop-blur-sm shadow-sm;
  }

  /* Smooth transitions */
  .card,
  .button,
  .link {
    @apply transition-all duration-200;
  }

  /* Static brand colors */
  .color-burgundy {
    color: rgb(var(--color-burgundy));
  }

  .color-orange {
    color: rgb(var(--color-orange));
  }

  .color-turquoise {
    color: rgb(var(--color-turquoise));
  }

  .bg-burgundy {
    background-color: rgb(var(--color-burgundy));
    color: white;
  }

  .bg-orange {
    background-color: rgb(var(--color-orange));
    color: white;
  }

  .bg-turquoise {
    background-color: rgb(var(--color-turquoise));
    color: white;
  }
}

