import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Logo } from "@/components/ui/logo";
import { useToast } from "@/hooks/use-toast";
import { Checkbox } from "@/components/ui/checkbox";

export default function AdminLogin() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [rememberMe, setRememberMe] = useState(false);
  const [, setLocation] = useLocation();
  const { toast } = useToast();

  // Load saved credentials on component mount
  useEffect(() => {
    const savedCredentials = localStorage.getItem("adminCredentials");
    if (savedCredentials) {
      const { username: savedUsername, password: savedPassword } = JSON.parse(savedCredentials);
      setUsername(savedUsername);
      setPassword(savedPassword);
      setRememberMe(true);
    }
  }, []);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    // For demo purposes, using simple credentials
    if (username === "admin" && password === "kestia2024") {
      localStorage.setItem("adminAuth", "true");

      // Save or clear credentials based on remember me checkbox
      if (rememberMe) {
        localStorage.setItem("adminCredentials", JSON.stringify({
          username,
          password
        }));
      } else {
        localStorage.removeItem("adminCredentials");
      }

      setLocation("/admin/dashboard");
    } else {
      toast({
        title: "Error",
        description: "Invalid username or password",
        variant: "destructive",
      });
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center pb-32">
      <Logo />
      <div className="w-full max-w-md">
        <h1 className="text-2xl font-bold text-center text-[#872519] mb-8">Staff Login</h1>
        <form onSubmit={handleSubmit} className="space-y-4">
          <Input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Username"
            className="w-full"
          />
          <Input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Password"
            className="w-full"
          />
          <div className="flex items-center space-x-2">
            <Checkbox
              id="remember"
              checked={rememberMe}
              onCheckedChange={(checked) => setRememberMe(checked as boolean)}
            />
            <label
              htmlFor="remember"
              className="text-sm font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70"
            >
              Remember me
            </label>
          </div>
          <Button type="submit" className="w-full">
            Login
          </Button>
        </form>
      </div>
    </div>
  );
}

server/migrate-data.ts.content:
import { db } from "./db";
import { menuItems } from "@shared/schema";
import { promises as fs } from 'fs';
import path from 'path';

async function migrateData() {
  console.log("Starting data migration...");

  // Read JSON files
  const menuItemsPath = path.join(process.cwd(), 'shared/menu-items.json');
  const drinksPath = path.join(process.cwd(), 'shared/drinks.json');

  const menuData = JSON.parse(await fs.readFile(menuItemsPath, 'utf-8'));
  const drinksData = JSON.parse(await fs.readFile(drinksPath, 'utf-8'));

  // Prepare all menu items
  const allItems = [];
  let foodId = 1;

  // Process food items (excluding vegetarian as it's a derived view)
  for (const [category, items] of Object.entries(menuData)) {
    if (category === "vegetarian" || !Array.isArray(items)) continue;

    for (const item of items as any[]) {
      allItems.push({
        name: item.name,
        description: item.description || "",
        price: item.price,
        category: category,
        imageUrl: item.imageUrl || null,
        isSpecial: item.isSpecial || false
      });
      foodId++;
    }
  }

  console.log(`Found ${allItems.length} food items`);

  // Process drinks starting at ID 1000
  let drinkId = 1000;
  for (const [category, items] of Object.entries(drinksData)) {
    if (!Array.isArray(items)) continue;

    for (const item of items as any[]) {
      allItems.push({
        name: item.name,
        description: item.description || "",
        price: item.price,
        category: category,
        imageUrl: item.imageUrl || null,
        isSpecial: item.isSpecial || false
      });
      drinkId++;
    }
  }

  console.log(`Found ${drinkId - 1000} drink items`);
  console.log(`Total items to migrate: ${allItems.length}`);

  // Insert all items into database
  if (allItems.length > 0) {
    // Clear existing data first
    await db.delete(menuItems);
    console.log("Cleared existing menu items");

    // Insert new data in batches
    const batchSize = 50;
    for (let i = 0; i < allItems.length; i += batchSize) {
      const batch = allItems.slice(i, i + batchSize);
      await db.insert(menuItems).values(batch);
      console.log(`Inserted batch ${Math.floor(i / batchSize) + 1}/${Math.ceil(allItems.length / batchSize)}`);
    }
  }

  console.log("Data migration completed successfully!");
  console.log(`Total items migrated: ${allItems.length}`);
}

migrateData()
  .then(() => process.exit(0))
  .catch((error) => {
    console.error("Migration failed:", error);
    process.exit(1);
  });

